$(document).ready(function() {

  function hideAll() {
    $('#songOne').hide();
    $('#songTwo').hide();
    $('#songThree').hide();
    $('#songFour').hide();
  }

  hideAll();

  $('.albumCovers').click(function() {

    hideAll();
    switch ($(this).attr("id")) {
     case "theFame":
       $('#songOne').show();
       break;
     case "theFameMonster":
       $('#songTwo').show();
       break;
     case "bornThisWay":
       $('#songThree').show();
       break;
     case "johanna":
       $('#songFour').show();
       break;
     }
  $("audio").each(function() {
    this.pause();
    this.currentTime = 0;

  $('#coverAlbums').click(function() {
    $('#summary').slideToggle("slow");

});

});


  });

});
